import React from 'react';
import {Button,message} from 'antd';
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import './CreateArticle.css';

// componenet Import
import CoverPhotoUploader from './CoverPhotoUploader/CoverPhotoUploader';
import InputTitle from './InputTitle/InputTitle'

const CreateArticle = (props) => {
    const success = () => {
        message.success('Your article was saved succefly!!');
      };

    return(
        <>
            <Select placeholder="Categories" name="category" style={{ width: 200 }} onChange={(e) => SelectCategory(e)}>
                <Option value="House">House</Option>
                <Option value="Teck">Teck</Option>
                <Option value="Hobbies">Hobbies</Option>
            </Select>
            <CoverPhotoUploader/>
            <InputTitle/> 
            <Button className="SaveBtn" type="primary" onClick={success}>
                Save
            </Button>
            {/* <Button type="primary">Save</Button> */}

            <CKEditor
                    editor={ ClassicEditor }
                    data="<p>Hello from CKEditor 5!</p>"
                    onInit={ editor => {
                        // You can store the "editor" and use when it is needed.
                        console.log( '<strong> Editor is ready to use! </strong>', editor );
                    } }
                    onChange={ ( event, editor ) => {
                        const data = editor.getData();
                        console.log( { event, editor, data } );
                    } }
                    onBlur={ ( event, editor ) => {
                        console.log( 'Blur.', editor );
                    } }
                    onFocus={ ( event, editor ) => {
                        console.log( 'Focus.', editor );
                    } }
            />
        </>
    );
}

 export default CreateArticle;
