import React from 'react';
import {Card, Row, Col, Avatar} from 'antd';
import { HeartOutlined, CommentOutlined } from '@ant-design/icons'
import './Article.css';

const { Meta } = Card;



const Article = (props) => {
    return (
        <Card
        hoverable
        style={{ width: 240 }}
        cover={<img alt="example" shape="circle" src="https://assets.entrepreneur.com/content/3x2/2000/20191219170611-GettyImages-1152794789.jpeg" />}
        >
        <Meta title="Yamma lasmar douni" description="balak togo l3lach lasmardouni 3lach ya ziman 3lach Alach 3lach " />
        <Row  className="container " >
            <Col className=' heartComment' span={12}>
                <HeartOutlined /> 45
            </Col>
            <Col className='heartComment' span={12}>
                <CommentOutlined /> 143
            </Col>
        </Row>

        <Row className="Infos" >
            <Col  className='gutter-row NameDate' span={4}>
                <Avatar  size="large" src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
            </Col>
            <Col className='gutter-row NameDate container' span={20}>
                    <p> 
                        Layca Yakoubi 
                            <br/>
                        12 Aug 2020
                    </p>
            </Col>
        </Row>
      </Card>
    );

}

export default Article;
