import React from 'react';
import  {Tag, Typography,Row, Col} from 'antd';
import { HeartOutlined, CommentOutlined } from '@ant-design/icons';
import './ShowArticle.css';

const {Title} = Typography
const ShowArticle = (props) =>{





    return (
        <>
            <div className="ImageShow" style={{backgroundImage: `url(${props.data.articlePicUrl})`}}>
                <Row className='LikeCmntBox'>
                    <Col lg={24} >
                        <div className=' heartComment' span={12} style={{color :'deeppink'}}>
                            <a><HeartOutlined  style={{color :'deeppink'}} /> {props.data.hearts} </a>
                        </div>
                    </Col>

                    <Col lg={24} >
                         <div className='heartComment' style={{color : 'steelblue'}} span={12}>
                            <a><CommentOutlined /> {props.data.comments}</a>
                        </div>
                    </Col>
                </Row>
            </div>
            <Title>{props.data.title}</Title>
            <div>
                {props.data.content}
            </div>



            {props.data.tag.map((key) =>( <Tag key={key} color={key}>{key}</Tag> ))}


        </>
    );
}

export default ShowArticle;
