import React from 'react';
import {Card, Row, Col, Avatar} from 'antd';
import { HeartOutlined, CommentOutlined } from '@ant-design/icons'
import './Article.css';

const { Meta } = Card;


// {
//     articlePicUrl : 'https://assets.entrepreneur.com/content/3x2/2000/20191219170611-GettyImages-1152794789.jpeg',
//     title : 'samir losif jarhouni ',
//     description : 'datni sikra w datni w ena baghik ya li7biba min ghir matgolili je theme',
//     hearts  : 325,
//     comments : 543,
//     AuthorName : 'Layca sahboni',
//     Date : '12 Jav 2012',
//     AuthorAvatar:   'https://images.theconversation.com/files/304076/original/file-20191127-112499-1cv5azx.jpg' ,


//   },

const Article = (props) => {
    return (
        <Card
        hoverable
        style={{ width: 240 }}
        cover={<img alt="example" shape="circle" src={props.data.articlePicUrl} />}
        >
        <Meta title={props.data.title} description={props.data.description} />
        <Row  className="container " >
            <Col className=' heartComment' span={12} style={{color :'deeppink'}}>
                <HeartOutlined  /> {props.data.hearts}
            </Col>
            <Col className='heartComment' style={{color : 'steelblue'}} span={12}>
                <CommentOutlined /> {props.data.comments}
            </Col>
        </Row>

        <Row className="Infos" >
            <Col  className='gutter-row NameDate' span={4}>
                <Avatar  size="large" src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
            </Col>
            <Col className='gutter-row NameDate container' span={20}>
                    <p> 
                        Layca Yakoubi 
                            <br/>
                        12 Aug 2020
                    </p>
            </Col>
        </Row>
      </Card>
    );

}

export default Article;
