import React, {useState} from 'react';
import {Image, Space, } from 'antd';
import './CoverPhotoUploader.css';
import InputTitle from './InputTitle/InputTitle';
import { render } from '@testing-library/react';

const CoverPhotoUploader = (props) =>
{
    const [Image, setImage] = useState({file: '',imagePreviewUrl: 'https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png'});

    const xts = useRef(null);

    const imageUploadHandler = e =>
    {
        console.log(e.target.files[0]);
        let render = new FileReader();


        e.preventDefault();

        let reader = new FileReader();
        let file = e.target.files[0];

        reader.onloadend = () => {
            setImage({
            file: file,
            imagePreviewUrl: reader.result
          });
        }
        reader.readAsDataURL(file)

    }

    const clickedImage = ()=>{
        xts.current.click();
        // uploadBtn.onChange() 
    }  
    const uploadBtn = (<input ref={xts} type='file' onChange={imageUploadHandler} />);
    return (
        <div  className="ImageShow" style={{backgroundImage: `url(${Image.imagePreviewUrl})`}} onClick={clickedImage}>

            <InputTitle/>
            {uploadBtn}

        </div>
    );
}

export default CoverPhotoUploader
